import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { forkJoin } from 'rxjs';
import { PokeapiService } from 'src/app/service/pokeapi.service';

@Component({
  selector: 'app-detalhes',
  templateUrl: './detalhes.component.html',
  styleUrls: ['./detalhes.component.css']
})
export class DetalhesComponent implements OnInit {

  private urlPokemon: string = 'https://pokeapi.co/api/v2/pokemon';
  private urlName: string = 'https://pokeapi.co/api/v2/pokemon-species';

  public isLoading: boolean = false;
  public apiError: boolean = false;


  public pokemonDetalhes: any;
  public coresPokemon : any;

  constructor(
    private activatedRoute: ActivatedRoute,
    private pokeapiService: PokeapiService,
  ){}

  ngOnInit(): void {
    this.UnicoPokemon();
  }

  public UnicoPokemon(){
    const id = this.activatedRoute.snapshot.params['id'];
    const pokemon = this.pokeapiService.apiGetPokemons(`${this.urlPokemon}/${id}`);
    const name = this.pokeapiService.apiGetPokemons(`${this.urlName}/${id}`);

    return forkJoin([pokemon,name]).subscribe(
      res =>{
        this.pokemonDetalhes = res;
        this.coresPokemon = res[1].color.name;
        this.isLoading = true;
      },
      error  =>{
        this.apiError = true;
      }

    )
  }
}
<poke-header></poke-header>

<main>
    <!-- <ng-template [ngIf]="!isLoading && !apiError">
        <img class="laoderpikachu" src="assets/pikachu.gif">
        <div class="loading-spinner">
            <div class="loading-spinner-inner">
                <div class="loading-spinner-circle"></div>
                <div class="loading-spinner-circle"></div>
                <div class="loading-spinner-circle"></div>
                <div class="loading-spinner-circle"></div>
                <div class="loading-spinner-circle"></div>
            </div>
        </div>
    </ng-template> -->

    <ng-template [ngIf]="isLoading">
        <div class="nomePokemon">
            <h1 class="fadeInDown">{{pokemonDetalhes[1].names[0].name}}</h1>
            <h2 class="fadeInDown">{{pokemonDetalhes[0].name}}</h2>
        </div>

        <section class="cardPokemon fadeIn">
            <div class="header-card">
                <img class="fadeInDown" [src]="pokemonDetalhes[0].sprites.other.dream_world.front_default" alt="pokemonDetalhes[0].name">
            </div>
            <h1 [ngStyle]="{'color': coresPokemon}">Estatisticas:</h1>
            <ul>
                <li *ngFor="let value of pokemonDetalhes[0].stats">
                    <b>{{value.stat.name | uppercase}}</b>
                    <b>{{value.base_stat}}</b>
                </li>
            </ul>
            <a [ngStyle]="{'background-color': coresPokemon}" routerLink="" class="btn">Voltar</a>
        </section>
    </ng-template>

    <ng-template [ngIf]="apiError">
        <h1 class="error-title">Parece que nossa pokedex não esta funcionando</h1>
    </ng-template>
</main>
