import { Component, OnInit } from '@angular/core';
import { PokeapiService } from 'src/app/service/pokeapi.service';

@Component({
  selector: 'list-pokemon',
  templateUrl: './list-pokemon.component.html',
  styleUrls: ['./list-pokemon.component.css']
})
export class ListPokemonComponent implements OnInit{
  private setAllPokemons: any;
  public getAllPokemons: any;

  constructor(private pokeapiService: PokeapiService){}



  ngOnInit(): void {
    this.pokeapiService.apiListAllPokemons.subscribe(
      res => { 
        this.setAllPokemons = res.results;
        this.getAllPokemons = this.setAllPokemons;
        console.log(this.getAllPokemons)
      }
    )  
  }

  public getSearch(value:string){
    const filter = this.setAllPokemons.filter( (res:any) => {
      return !res.name.indexOf(value.toLocaleLowerCase())
    })
   this.getAllPokemons = filter
  }
}
<busca-pokemon (emmitSearch)="getSearch($event)"></busca-pokemon>
<section class="flex-grid">
    <ng-template ngFor let-pokemon [ngForOf]='getAllPokemons'>
        <a [routerLink]="['detalhes', pokemon.status.id]" class="cards" *ngIf="pokemon.status">
            <div class="caracteristicas">
                <h2>{{pokemon.name}} </h2>
                <ul>
                    <li *ngFor="let value of pokemon.status.types">
                        {{value.type.name}}
                    </li>
                </ul>
            </div>
            <img [src]="pokemon.status.sprites.other.dream_world.front_default" [alt]="pokemon.name">
        </a>
    </ng-template>
</section>
